import React, {Component} from 'react';
import { Modal, ModalHeader, ModalBody, ModalFooter} from 'reactstrap';
import Button from '../Button/Button';
import "./Popup.scss";

class Popup extends Component {
  state = {
    modal: false
  }

  toggleHandler = () => {
    this.setState(prevState => ({
      modal: !prevState.modal
    }))
  }

  render() {
    const {isOn} = this.props;
    return(
      <div className="popup">
        <img className="popup__img" src="https://images.unsplash.com/photo-1506994011460-5482746d30a1?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=634&q=80" alt=""/>
        <p className="popup__type">Web App</p>
        <h3 className="popup__title">A music store</h3>
        <p className="popup__text">A music store descrption</p>
        <Button onClick={this.toggleHandler}>View Project</Button>
      </div>
    )
  }
}

export default Popup
