import React, { Component } from 'react';
import Transition from 'react-transition-group';

export default class TransitionExample extends Component {
  state = {
    show: false,
    entered: false,
  };

  render() {
    const { show } = this.state;
    return (
      <div style={{ paddingTop: '2rem' }}>
        <button
          onClick={() => {
            this.setState(state => ({
              show: !state.show,
            }));
          }}
        >
          Toggle
        </button>
          <Transition
            in={show}
            timeout={1000}
            unmountOnExit
          >
            {state => {
              switch (state) {
                case 'entering':
                  return 'Entering…';
                case 'entered':
                  return 'Entered!';
                case 'exiting':
                  return 'Exiting…';
                case 'exited':
                  return 'Exited!';
              }
            }}
          </Transition>
      </div>
    );
  }
}
